        return new Source("{% extends 'base.html.twig' %}

{% block title %}Products{% endblock %}

{% block body %}
    <section class=\"mt-3\">
        <div class=\"row\">
            {{ include('inc/_cart_total.html.twig') }}
        </div>
        <div class=\"row\">
            {% if products %}
                {% for product in products %}
                    <div class=\"col-3\">
                        <div class=\"card border-primary text-center mb-1\" style=\"max-width: 20rem;\">
                            <div class=\"card-body \">
                                <h4 class=\"card-title\">{{ product.name }}</h4>
                                <span class=\"badge badge-dark\">{{ product.getProductCategory.getName }}</span>
                                <p class=\"card-text\">{{ product.description }}</p>
                                <h5 class=\"text-center\">Rs: {{ product.price |number_format(2, '.', ',') }}</h5>
                                {{ include('inc/_add_to_cart.html.twig') }}
                            </div>
                        </div>
                    </div>
                {% endfor %}
            {% else %}
                <h4>No Products Found!</h4>
            {% endif %}
        </div>
    </section>
{% endblock %}
", "shop/index.html.twig", "/mnt/b4e33210-d0d5-464b-bd55-b75dd455ba8d/book_store/templates/shop/index.html.twig");
